import { useRef, useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import './styles/chat.css';

export default function Chat(props) {
    const [messages, setMessages] = useState([]);
    const [io] = useState(useSelector((state) => state.socket.value));
    const textarea = useRef(null);

    useEffect(() => {
        const id = props.match.params.id;
        io.on('message-received', msg => {
            setMessages([...messages, msg]);
        });
    });

    function handleSubmit(e) {
        e.preventDefault();
        const msg = textarea.current.value;
        setMessages([...messages, msg]);
        io.emit('message', {id: id, msg: msg});
    }

    return (
        <div class="chat">
            <div class="chat-title">Chat</div>
            <div class="chat-body">
            { messages.map(value => 
                <div class="bubble you">
                    { value }
                </div>
            )}
            </div>
            <div class="message-box">
                <textarea type="text" class="message-input" ref={textarea} placeholder="Type message..."></textarea>
                <button type="submit" class="message-submit" onSubmit={handleSubmit}>Send</button>
            </div>
        </div>
    );
}
