import React, {Component} from 'react';
import './styles/player.css';
import { io } from 'socket.io-client';
import Peer from 'peerjs';

export default class Player extends Component {
    constructor(props){
        super(props)
        this.io = io("http://localhost:8000/");
        this.video = React.createRef();
    }

    getUserMedia() {
        if(this.state.initiate){
            return navigator.mediaDevices.getUserMedia({
                video: {
                  mediaSource: "screen",
                  width: { max: '1920' },
                  height: { max: '1080' },
                  frameRate: { max: '30' }
                }
              });
        }
    }

    handleClient(stream){
        const call = this.peer.call(this.props.match.params.id, stream);
        this.video.current.srcObject = stream;
        this.video.current.play();
    } 

    handleServer(stream){
        this.peer.on('call', call => {
            call.answer(stream);
            this.video.current.srcObject = stream;
            this.video.current.play();
        }, err => {
            console.log('failed to answer!');
        })
    }

    componentDidMount() {
        const id = this.props.match.params.id;
        fetch('http://localhost:8000/isStarter', {
            body: JSON.stringify({id: id})
        })
        .then(res => res.json())
        .then(({initiate}) => {
            if(initiate){
                this.peer = new Peer(id);
                this.getUserMedia().then(this.handleServer);
            }else {
                this.peer = new Peer();
                this.getUserMedia().then(this.handleClient);
            }
        })
    }

    componentWillUnmount() {
        this.peer.disconnect();
    }
    
    render(){
        return (
            <div class="player">
                <div class="title"><h3>Salam</h3> <img src="live.png"/></div>
                <video ref={this.video} autoPlay/>
            </div>
        )
    }
}
