import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import Player from './player';
import { setName } from '../redux/slicers/nameSlicer';

export default function Main(props) {
    const [visible, setVisible] = useState(false);
    const [value, setValue] = useState();
    const dispatch = useDispatch();
    const [initiate] = useState(new URLSearchParams(props.location.search).get('streamer'));

    function openModal() {
        setVisible(true);
    }

    function closeModal() {
        setVisible(false);
    }

    function handleClick(e) {
        e.preventDefault();
        if (!value) {
            return alert('Esmet Chie??');
        }
        dispatch(setName(value));
        closeModal();
    }

    useEffect(() => {
        if (initiate){
            openModal();
        }
    });
    
    return (
        <>
            <Player {...props} initiate={initiate} />
            <Chat {...props} />
            <Modal isOpen={visible}>
                <div class="home">
                    <h1>Enter your name</h1>
                    <textarea type="text" class="name-input" onChange={(e) => { setValue(e.target.value) }} placeholder="Esmet Chie?..."></textarea>
                    <button onClick={handleClick}>Berim</button>
                </div>
            </Modal>
        </>
    )
}
