import {useState, useEffect, useRef } from 'react';
import { CustomDialog, useDialog } from 'react-st-modal';
import { useSelector, useDispatch } from 'react-redux';
import { setName } from '../redux/slicers/nameSlicer';
import './styles/chat.css';

export default function Chat(props) {
    const [messages, setMessages] = useState([]);
    const [io] = useState(useSelector((state) => state.socket.value));
    const [value, setValue] = useState(null);
    const name = useSelector((state) => state.name.value);
    const chat = useRef(null);

    useEffect(async () => {
        const result = await CustomDialog(
            <NameDialog />,
            {
              title: 'Enter your name',
              showCloseIcon: true,
            }
          );
        dispatch(setName(result));
        io.on('message-received', msg => {
            setMessages([...messages, msg]);
        });

        io.on('user-connected', ({name}) => {
            chat.current.innerHTML += `<span>${name} connected!</span>`
        });
    });

    function handleChange(e) {
        setValue(e.target.value);
    }

    function handleSubmit(e) {
        e.preventDefault();
        const id = props.match.params.id;
        setMessages([...messages, value]);
        io.emit('message', {id: id, msg: value});
        console.log(value)
    }

    return (
        <div class="chat">
            <div class="chat-title">Chat</div>
            <div class="chat-body" ref={chat}>
            { messages.map(value => 
                <div class="bubble you">
                    <span>{name}: </span>{ value }
                </div>
            )}
            </div>
            <form class="message-box" onSubmit={handleSubmit}>
                <textarea type="text" class="message-input" onChange={handleChange} placeholder="Type message..."></textarea>
                <button type="submit" class="message-submit">Send</button>
            </form>
        </div>
    );
}

function NameDialog(props) {
    const dialog = useDialog();
    const [value, setValue] = useState();
    const dispatch = useDispatch();

    function handleClick(e) {
        if(!value){
            return alert('Bache esmet Chie??');
        }
        dialog.close(value);
    }

    return (
        <div class="home">
            <h1>Enter your name</h1>
            <textarea type="text" class="name-input" onChange={(e) => { setValue(e.target.value) }} placeholder="Esmet Chie?..."></textarea>
            <button onClick={handleClick}>Berim</button>
        </div>
    );
}
