import React, { useState, useEffect, useRef } from 'react';
import Peer from 'peerjs';
import { useSelector, useDispatch } from 'react-redux';
import './styles/player.css';
import { disconnect } from '../redux/slicers/socketSlicer';

function getUserMedia() {
    return navigator.mediaDevices.getDisplayMedia({
            video: {
              mediaSource: "screen",
              width: { max: '1920' },
              height: { max: '1080' },
              frameRate: { max: '30' }
        }
    });
} 

let peer = null;

export default function Player(props) {
        const video = useRef(null);
        const [io] = useState(useSelector((state) => state.socket.value));
        const [initiate] = useState(new URLSearchParams(props.location.search).get('streamer'))
        const dispatch = useDispatch();

    useEffect(() => {
        const id = props.match.params.id;
        console.log(initiate)

        if (initiate){
            peer = new Peer(id);
            peer.on('open', () => {
                io.emit('join', {id: id, peerid: id})
                getUserMedia().then(stream => {
                    video.current.srcObject = stream;
                    video.current.play();
                    peer.on('connection', () => {
                        io.on('user-connected', ({peerid}) => {
                            console.log('called')
                            peer.call(peerid, stream);
                        });
                    });
                });
            })
        } else {
            peer = new Peer();
            const conn = peer.connect(id);
            console.log(peer);
            peer.on('open', () => {
                peer.on('call', call => {
                    console.log(peer._id);
                    call.on('stream', remoteStream => {
                        video.current.srcObject = remoteStream;
                        video.current.play();
                    });
                });
                io.emit('join', {id: id, peerid: peer._id});
                console.log('joined')
            });
            peer.on('error', () => {
                console.log('ride shod tush');
            })
        }

        console.log(`User with id ${id} connected!`);
       
        return () => {
            dispatch(disconnect());
        };

    }, [props.match.params.id, io, initiate, peer, video, dispatch]);
    
    return (
        <div class="player">
            <div class="title"><h3>Salam</h3> <img alt=' ' id="live" src="live.png"/></div>          
            <video ref={video} autoPlay/>      
        </div>
    )
}
