import React, {Component} from 'react';
import './styles/player.css';
import { io } from 'socket.io-client';
import Peer from 'peerjs';

export default class Player extends Component {
    constructor(props){
        super(props)
        this.io = io("http://localhost:8000");
        this.video = React.createRef();
        this.state = { initiate: new URLSearchParams(this.props.location.search).get('streamer')};
        
        this.handleClient = this.handleClient.bind(this);
        this.handleServer = this.handleServer.bind(this);
    }

    getUserMedia() {
        return navigator.mediaDevices.getDisplayMedia({
                video: {
                  mediaSource: "screen",
                  width: { max: '1920' },
                  height: { max: '1080' },
                  frameRate: { max: '30' }
            }
        });
    }

    handleClient(){
        this.io.on('start', stream => {
            this.video.current.srcObject = stream;
            this.video.current.play();
        });
    } 

    handleServer(stream){
        this.video.current.srcObject = stream;
        this.video.current.play();
        this.io.emit('stream', stream);
    }

    componentDidMount() {
        const id = this.props.match.params.id;
        this.io.broadcast.emit('start', id);
        if(this.state.initiate){
            this.getUserMedia(this.handleServer);
        } else {
            this.handleClient();
        }

       // .then(res => res.json())
       // .then(({initiate}) => {
       //     console.log(initiate)
       //     if(initiate){
       //         this.getUserMedia().then(this.handleServer);
        //    }else {
       //         this.handleClient();
       //     }
      //  })
    }

    componentWillUnmount() {
        this.io.disconnect();
    }
    
    render(){
        return (
            <div class="player">
                <div class="title"><h3>Salam</h3> <img src="live.png"/></div>
                <video ref={this.video} autoPlay/>
            </div>
        )
    }
}
