import React, { useState, useEffect } from 'react';
import Peer from 'peerjs';
import { useSelector, useDispatch } from 'react-redux';
import './styles/player.css';
import { disconnect } from '../redux/slicers/socketSlicer';

function getUserMedia() {
    return navigator.mediaDevices.getDisplayMedia({
            video: {
              mediaSource: "screen",
              width: { max: '1920' },
              height: { max: '1080' },
              frameRate: { max: '30' }
        }
    });
} 

export default function Player(props) {
        const video = React.createRef();
        const [io] = useState(useSelector((state) => state.socket.value));
        const [peer, setPeer] = useState(null);
        const [initiate] = useState(new URLSearchParams(props.location.search).get('streamer'))
        const dispatch = useDispatch();

    useEffect(() => {
        const id = props.match.params.id;
        io.emit('join', this.peer.id);

        if (initiate){
            peer = new Peer(id);
            peer.on('call', call => {
                getUserMedia().then(stream => {
                    video.current.srcObject = stream;
                    video.current.play();
                    call.answer(stream);
                })
            })
        } else {
            peer = new Peer().connect(id);
        }
        io.on('user-connected', ({initiate, peerid}) => {
            if (!initiate){
                let call = peer.call(id);
                call.on('stream', stream => {
                    video.current.srcObject = stream;
                    video.current.play();
                })
            }
            console.log(`User with id ${peerid} connected!`);
        });
       
        return () => {
            dispatch(disconnect());
        }
    });

    
    return (
        <div class="player">
            <div class="title"><h3>Salam</h3> <img id="live" src="live.png"/></div>          
            <video ref={this.video} autoPlay/>      
        </div>
    )
}
