import {Component} from 'react';
import './styles/player.css';
import { io } from 'socket.io-client';
import Peer from 'simple-peer';

const stunServerConfig = {
    iceServers: [{
      url: 'turn:13.250.13.83:3478?transport=udp',
      username: "YzYNCouZM1mhqhmseWk6",
      credential: "YzYNCouZM1mhqhmseWk6"
    }]
};

export default class Player extends Component {
    constructor(props){
        super(props)
        const socket = io("http://localhost:8000/").socket.io;
        this.io = socket;
        this.state = {initiate: false};

        socket.on('start', () => {
            this.startStream();
        });
    }

    handleMedia(stream) {
        let peer;
        if(this.state.initiate){
            peer = new Peer({initiate: this.state.initiate, stream: stream, config: stunServerConfig});
        } else {
            peer = new Peer({config: stunServerConfig});
        }

        peer.on('signal', data => {
            this.io.emit('offer', JSON.stringify(data));
        });

        this.io.on('offer', data => {
            peer.signal(JSON.parse(data));
        });

        peer.on('stream', stream => {
            this.src = stream;
        })
    }

    startStream() {
        if(this.state.initiate){
            navigator.mediaDevices.getUserMedia({
                video: {
                  mediaSource: "screen",
                  width: { max: '1920' },
                  height: { max: '1080' },
                  frameRate: { max: '10' }
                }
              }).then(this.handleMedia)
              .catch(err => {throw err});
        }
    }

    componentDidMount() {
        this.setState({initiate: true});
        this.io.emit('start');
    }

    componentWillUnmount() {
        this.io.emit('start');
    }
    
    render(){
        return (
            <div class="player">
                <div class="title"><h3>Salam</h3> <img src="live.png"/></div>
                <video autoPlay ref={vid => { vid.srcObject = this.src }}/>
            </div>
        )
    }
}
